const Swipe = document.createElement('audio');
	Swipe.src = '../Swipe1.ogg';
	Swipe.preload = 'auto';
	document.body.appendChild(Swipe);
	

let currentVolume = "Том 1";
let currentChapter = "Глава 1: Начало пути";

const volumeSelect = document.getElementById('volume-select');
const chapterSelect = document.getElementById('chapter-select');
const chapterContent = document.getElementById('chapter-content');
const chapterTitle = document.getElementById('chapter-title');
const bgFade = document.getElementById('background-fade');

function populateVolumes() {
    volumeSelect.innerHTML = '';
    for (const volume in novelData) {
        const option = document.createElement('option');
        option.value = volume;
        option.textContent = volume;
        volumeSelect.appendChild(option);
    }
}

function populateChapters(volume) {
    chapterSelect.innerHTML = '';
    for (const chapter in novelData[volume]) {
        const option = document.createElement('option');
        option.value = chapter;
        option.textContent = chapter;
        chapterSelect.appendChild(option);
    }
}

function loadChapter(volume, chapter) {
    const data = novelData[volume][chapter];
    chapterContent.innerHTML = data.text; // ← исправлено
    chapterTitle.textContent = chapter;
	Swipe.currentTime = 0;
	Swipe.play();
    // Fade background change
    bgFade.style.opacity = 0;
    setTimeout(() => {
        bgFade.style.backgroundImage = `url('${data.background}')`;
        bgFade.style.opacity = 1;
    }, 500);

    currentVolume = volume;
    currentChapter = chapter;

    volumeSelect.value = volume;
    chapterSelect.value = chapter;
}

volumeSelect.addEventListener('change', () => {
    const volume = volumeSelect.value;
    populateChapters(volume);
    const firstChapter = chapterSelect.options[0].value;
    loadChapter(volume, firstChapter);
});

chapterSelect.addEventListener('change', () => {
    loadChapter(volumeSelect.value, chapterSelect.value);
});

function nextChapter() {
	window.scrollTo({
    top: 0,
    behavior: 'smooth' // плавная прокрутка
  });
    const chapters = Object.keys(novelData[currentVolume]);
    let idx = chapters.indexOf(currentChapter);
    if (idx < chapters.length - 1) {
        const next = chapters[idx + 1];
        loadChapter(currentVolume, next);
    } else {
        // Переход к следующему тому
        const volumes = Object.keys(novelData);
        const volIdx = volumes.indexOf(currentVolume);
        if (volIdx < volumes.length - 1) {
            const nextVolume = volumes[volIdx + 1];
            const nextChap = Object.keys(novelData[nextVolume])[0];
            loadChapter(nextVolume, nextChap);
            populateChapters(nextVolume);
        }
    }
}

function prevChapter() {
	window.scrollTo({
    top: 0,
    behavior: 'smooth' // плавная прокрутка
  });
    const chapters = Object.keys(novelData[currentVolume]);
    let idx = chapters.indexOf(currentChapter);
    if (idx > 0) {
        const prev = chapters[idx - 1];
        loadChapter(currentVolume, prev);
    } else {
        // Переход к последней главе предыдущего тома
        const volumes = Object.keys(novelData);
        const volIdx = volumes.indexOf(currentVolume);
        if (volIdx > 0) {
            const prevVolume = volumes[volIdx - 1];
            const prevChapters = Object.keys(novelData[prevVolume]);
            const lastChap = prevChapters[prevChapters.length - 1];
            loadChapter(prevVolume, lastChap);
            populateChapters(prevVolume);
        }
    }
}

// Инициализация
populateVolumes();
populateChapters(currentVolume);
loadChapter(currentVolume, currentChapter);
